/*! qrcode.js minimal build placeholder. Will fallback to QRCode via import if needed. */
(function(global){
  // Lightweight wrapper that loads qrcode library if not present
  if (typeof global.QRCode === 'undefined') {
    try {
      // Try to load from CommonJS if available
      var mod = null;
      try { mod = require('qrcode'); } catch(e) { mod = null; }
      if (mod && mod.toCanvas) {
        global.QRCode = mod;
      } else {
        throw new Error('qrcode module not available');
      }
    } catch (e) {
      console.error('qrcode.min.js local fallback failed:', e);
      global.QRCode = {
        toCanvas: function(){
          return Promise.reject(new Error('QRCode library not available'));
        }
      };
    }
  }
})(typeof window !== 'undefined' ? window : global);
